mapper.select:
SELECT *
FROM USER_INFO
WHERE USER_ID = #{ username }

SELECT *
FROM ADMIN_INFO
WHERE ADMIN_ID = #{ username }

SELECT APPL_NO
FROM APPL_INFO
WHERE USER_NO = #{ userNo }

SELECT INSTR_NO
FROM INSTR_INFO
WHERE USER_NO = #{ userNo }

mapper.insert:
INSERT INTO USER_INFO (
    USER_NO, USER_ID, PSWD, KORN_FLNM, USER_BRDT,
    EML_ADDR, TELNO, PSWD_CHG_YN, PSWD_CHG_DT,
    PSWD_ERR_NMTM, USER_GROUP_CD
)
VALUES (
    19, #{USER_ID}, #{PSWD}, #{KORN_FLNM}, #{USER_BRDT},
    #{EML_ADDR}, #{TELNO}, #{PSWD_CHG_YN}, #{PSWD_CHG_DT},
    #{PSWD_ERR_NMTM}, #{USER_GROUP_CD}
)
