mapper.select:
SELECT play_time
    FROM lecture
        WHERE
            video_id = #{video_id}
        AND
            student_no = #{student_no}

mapper.update:
UPDATE lecture
    SET
        play_time = #{curr_time}
WHERE
    video_id = #{video_id}
AND
    student_no = #{student_no}

mapper.select:
SELECT UV.SBJCT_NO, UV.SBJCT_NM, UV.KORN_FLNM, UV.CRCLM_NM
    FROM USER_APPL_VIEW as UV
        WHERE UV.APPL_NO = #{appl_no }
